ALTER TABLE `xaa`.`xaa_domains`
ADD COLUMN `address` TEXT NULL COMMENT '' AFTER `language`,
ADD COLUMN `phone` TEXT NULL COMMENT '' AFTER `address`;


CREATE TABLE `material_colors` (
  `color` varchar(45) NOT NULL DEFAULT '',
  `primary_color` varchar(7) DEFAULT NULL,
  `primary_dark` varchar(7) DEFAULT NULL,
  `primary_light` varchar(7) DEFAULT NULL,
  `font_color` varchar(7) DEFAULT NULL,
  `accent_color` varchar(7) DEFAULT NULL,
  `accent_font` varchar(7) DEFAULT NULL,
  `accent_color_a` varchar(7) DEFAULT NULL,
  `accent_font_a` varchar(7) DEFAULT NULL,
  `accent_color_b` varchar(7) DEFAULT NULL,
  `accent_font_b` varchar(7) DEFAULT NULL,
  PRIMARY KEY (`color`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;




CREATE TABLE `xaa`.`mkt_messages_usages` (
  `usage_id` INT(11) NOT NULL AUTO_INCREMENT,
  `name` VARCHAR(55) DEFAULT NULL,
  `date` DATETIME DEFAULT NULL,
  `subject` VARCHAR(100) DEFAULT NULL,
  `editor_message` TEXT,
  PRIMARY KEY (`usage_id`)
) ENGINE=INNODB DEFAULT CHARSET=latin1;


